function loadtable(){datatable=$("#questionTable").DataTable({processing:!0,serverSide:!0,filter:!0,responsive:!0,destroy:!0,ajax:{url:"/Home/QuestionTableLoader",type:"POST",datatype:"json"},columnDefs:[{visible:!0,searchable:!0}],columns:[{data:"id",render:function(n,t,i,r){return r.row+r.settings._iDisplayStart+1}},{data:"courseTable.name",autoWidth:!0},{data:"level",autoWidth:!0},{data:"semester",autoWidth:!0},{data:"question",render:function(n){return n.length>10?n.substring(0,50):n}},{data:null,render:function(n){return`
                        <div class="d-flex justify-content-center">
                         <a href="/Questions/Upsert?id=${n.id}" style="cursor:pointer" title="Edit Question" class="btn btn-primary btn-sm">
                                <i class="fa fa-edit"></i>
                            </a> &nbsp;&nbsp;
                            <a onclick=managequestion("/api/DeleteQuestion?id=${n.id}")  href="#" style="cursor:pointer" class="btn btn-danger btn-sm">
                                   <i class="fa fa-trash"></i>
                               </a>
                        </div>
                    `}}]})}function managequestion(n){const t=Swal.mixin({customClass:{confirmButton:"btn btn-success",cancelButton:"btn btn-danger"},buttonsStyling:!1});t.fire({title:"Are you sure?",text:"You won't be able to recover this!",icon:"warning",showCancelButton:!0,confirmButtonText:"Yes, delete it!",cancelButtonText:"No, cancel!",reverseButtons:!0}).then(t=>{t.isConfirmed?$.ajax({type:"DELETE",url:n,success:function(n){$.unblockUI();n.success?(swal.fire("Deleted!",n.message,"success"),loadtable()):swal.fire("Deleted!",n.message,"error")},beforeSend:function(){blockcallback()},error:function(){$.unblockUI();Swal.fire({icon:"error",title:"Oops...",text:"Something went wrong!",footer:"Check internet connectivity!"})},complete:function(){$.unblockUI()}}):t.dismiss===Swal.DismissReason.cancel&&swal.fire("Cancelled","Question NOT Deleted!","error")})}function blockcallback(){$.blockUI({message:'<div class="bs-spinner mt-4 mt-lg-0"><div class= "spinner-border text-success mr-2 mt-2" style="width: 10rem; height: 10rem;" role="status"><span class="sr-only">Loading...<\/span><\/div> ',fadeIn:800,overlayCSS:{backgroundColor:"#1b2024",opacity:.8,zIndex:1200,cursor:"wait"},css:{border:0,color:"#fff",zIndex:1201,padding:0,backgroundColor:"transparent"},onBlock:function(){}})}var classbtn="",classfa="",ap="";$(document).ready(function(){loadtable();$("#Question_Question").val("");$("#Question_OptionOne").val("");$("#Question_OptionTwo").val("");$("#Question_OptionThree").val("");$("#Question_OptionFour").val("");$("#Question_OptionFive").val("");$("#Question_Answer").val("")});