function loadtable(){$("#studentTable").DataTable({processing:!0,serverSide:!0,filter:!0,responsive:!0,destroy:!0,ajax:{url:"/Home/StudentTable",type:"POST",datatype:"json"},columnDefs:[{visible:!0,searchable:!0}],columns:[{data:"id",render:function(n,t,i,r){return r.row+r.settings._iDisplayStart+1}},{data:"fullName",autoWidth:!0},{data:"applicationUser.userName",autoWidth:!0},{data:"gender",autoWidth:!0},{data:"sessionTable.name",autoWidth:!0},{data:"applicationUser.status",render:function(n){return n==!0?(classfa="fa-unlock",classbtn="btn-success",n):(classfa="fa-lock",classbtn="btn-warning",n)}},{data:null,render:function(n){return`
                        <div class="d-flex justify-content-center">
                         <a class=" btn-secondary btn-sm" href="/Student-Manager/Registration/Upsert?id=${n.id}" style="cursor:pointer" title="Edit Student Information">
                                <i class="fa fa-edit"></i>
                            </a> &nbsp;
                            <a onclick=manageaccount("/api/StudentManager?id=${n.id}") class="btn ${classbtn} btn-sm" href="#" style="cursor:pointer" title="Lock/Unlock Account">
                                <i class="fa ${classfa}"></i>
                            </a> &nbsp;
                            <a onclick=Deletestudent("/api/DeleteStudent?id=${n.id}") class="btn btn-danger btn-sm" href="#" style="cursor:pointer">
                                   <i class="fa fa-trash"></i>
                               </a>
                        </div>
                    `}}]})}function Deletestudent(n){const t=Swal.mixin({customClass:{confirmButton:"btn btn-success",cancelButton:"btn btn-danger"},buttonsStyling:!1});t.fire({title:"Are you sure?",text:"You won't be able to recover this!",icon:"warning",showCancelButton:!0,confirmButtonText:"Yes, delete it!",cancelButtonText:"No, cancel!",reverseButtons:!0}).then(t=>{t.isConfirmed?$.ajax({type:"DELETE",url:n,success:function(n){$.unblockUI();n.success?(swal.fire("Deleted!",n.message,"success"),loadtable()):swal.fire("Deleted!",n.message,"error")},beforeSend:function(){blockcallback()},error:function(){$.unblockUI();Swal.fire({icon:"error",title:"Oops...",text:"Something went wrong!",footer:"Check internet connectivity!"})},complete:function(){$.unblockUI()}}):t.dismiss===Swal.DismissReason.cancel&&swal.fire("Cancelled","Student NOT Deleted!","error")})}function manageaccount(n){const t=Swal.mixin({customClass:{confirmButton:"btn btn-success",cancelButton:"btn btn-danger"},buttonsStyling:!1});t.fire({title:"Are you sure?",text:"The student account will be block/unblock?",icon:"warning",showCancelButton:!0,confirmButtonText:"Yes, Execute!",cancelButtonText:"No, cancel!",reverseButtons:!0}).then(t=>{t.isConfirmed?$.ajax({type:"POST",url:n,success:function(n){$.unblockUI();n.success?(swal.fire("Action Message!",n.message,"success"),loadtable()):swal.fire("Action Message!",n.message,"error")},beforeSend:function(){blockcallback()},error:function(){$.unblockUI();Swal.fire({icon:"error",title:"Oops...",text:"Something went wrong!",footer:"Check internet connectivity!"})},complete:function(){$.unblockUI()}}):t.dismiss===Swal.DismissReason.cancel&&swal.fire("Cancelled","Action aborted!","error")})}function blockcallback(){$.blockUI({message:'<div class="bs-spinner mt-4 mt-lg-0"><div class= "spinner-border text-success mr-2 mt-2" style="width: 10rem; height: 10rem;" role="status"><span class="sr-only">Loading...<\/span><\/div> ',fadeIn:800,overlayCSS:{backgroundColor:"#1b2024",opacity:.8,zIndex:1200,cursor:"wait"},css:{border:0,color:"#fff",zIndex:1201,padding:0,backgroundColor:"transparent"},onBlock:function(){}})}var classbtn="",classfa="",ap="";$(document).ready(function(){loadtable()});